import { PLAYLISTS_FETCHED } from '../actions/playlists'

const playlistsReducer = (state = [], action) => {
  switch (action.type) {
    case PLAYLISTS_FETCHED:
      return action.playlists
    default:
      return state
  }
}

export default playlistsReducer
