import React from 'react'

export default function PlaylistDetails(props) {
  return (
    <div>
      {!props.playlist.playlist && 'Loading playlist data...'}
      {props.playlist.playlist && <div>
        <h1>{props.playlist.name}</h1>
        <i>{props.playlist.date}</i>
        <p>{props.playlist.description}</p>
        <button onClick={props.onDelete}>Delete</button>
      </div>}
    </div>
  )
}
