import React from 'react'
import {Link} from 'react-router-dom'
import PlaylistFormContainer from './PlaylistFormContainer';

export default function Playlists(props) {
  console.log(props.playlists)
  return (
    <div>
      <h1>You have {props.playlists && props.playlists.length} Playlists!</h1>
      <ul>
        { props.playlists && props.playlists.map(playlists => (
          <li key={playlists.id}><Link to={`/playlists/${playlists.id}`}>
          { playlists.name }</Link></li>
        )) }
        { !props.playlists && <li>Loading playlists...</li> }
      </ul>
      <PlaylistFormContainer/>
    </div>
  )
}
