import React from 'react'
import {Link} from 'react-router-dom'
import PlaylistFormContainer from './PlaylistFormContainer';

export default function Playlists(props) {
  console.log(props.playlists)
  return (
    <div>
      <ul>
        { props.playlists && props.playlists.map(playlist => (
          <li key={playlist.id}><Link to={`/playlists/${playlist.id}`}>
          { playlist.playlistName }</Link> <button>Delete</button></li>
        )) }
        { !props.playlists && <li>Loading playlists...</li> }
      </ul>
      <PlaylistFormContainer/>
    </div>
  )
}
